  var imageurl = button.data('imageurl') // Extract info from data-* attributes Smag: adding this to grab image location for each modal
  var caption = button.data('caption')
  var modal = $(this)
  /* var magic = text('\"' + imageurl + '\"') */

  $('.modal-body img').attr({src:imageurl}) /* Halleluja */
	
